import React, { useState, useEffect} from 'react'
import Search from './searchBook'
import { BrowserRouter as Router } from "react-router-dom";

const App = () => {
  const [pageDetails, setData] = useState(null);
  const [currPage, setCurrPage] = useState(1);
  const { search } = window.location;
  const query = new URLSearchParams(search).get('book');

  // console.log(query);
  const fetched = seEffect(() => {
    // invalid url will trigger an 404 error
    fetch("/getpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(query)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    )
  }, [fetched]);

  function getNextPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage+1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    );
    setCurrPage(currPage + 1);
    console.log(currPage);
  }

  function getPrevPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage-1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    );
    setCurrPage(currPage - 1);
    console.log(currPage);
  }

  return <Router>
    {(
    <div>
      <Search/>
      <h1>Title: {pageDetails?.title}</h1>
      <h2>Page: {pageDetails?.pagenum}</h2>
      <h2>{pageDetails?.content}</h2>

      <button onClick={getPrevPage}>
        back page
      </button>
      <button onClick={getNextPage}>
        next page
      </button>
    </div>
  )} </Router>
}
export default App;
