import React, { useState, useEffect} from 'react';
import Page from './components/page';

import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import './App.css';
import { useHistory,useLocation } from "react-router-dom";
import Search from './components/searchBook'

const App = () => {
  // const [pageDetails, setData] = useState({title: '', pagenum: '', content: ''});

  const location = useLocation();
  const query = new URLSearchParams(location.search).get("book");

  const [title, setTitle] = useState(query?.replace(/\s/g, '-'));
  console.log(query);
  console.log(title);

  let history = useHistory();
  function handleClick(title: String) {
    history.push(`/page/${title}`);
  }
  useEffect(() => {
    if(title){
      // setData(data.pageById)
      //regex to remove space
      handleClick(title);
    }
  }, );

  return (
    <div>
      <Navbar/>
      <Router>
        <Switch>
          <Route path="/home">
            <Search />
            <Intro />
          </Route>
          <Route path={"/page"}>
            <SearchResult/>
            {/* <Search /> */}
          </Route>
        </Switch>
      </Router>
    </div>
  );
}
export default App;

