import React, { useState, useEffect} from 'react'
import Search from './searchBook'
import { BrowserRouter as Router } from "react-router-dom";

const updatePage = (pnum) => {
  return pnum + 1;
}

const App = () => {
  const [pageDetails, setData] = useState(null);
  const [nextPage, setNextPage] = useState(null);
  const { search } = window.location;
  const query = new URLSearchParams(search).get('book');
  let currPage = 1;
  let currTitle = "";
  // console.log(query);
  useEffect(() => {
    // invalid url will trigger an 404 error
    fetch("/getpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(query)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    )
  }, [])

  useEffect(() => {
    // invalid url will trigger an 404 error
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage+1)
    }).then(res => res.json()
    ).then(
      nextPage => {
        setNextPage(nextPage)
        console.log(nextPage)
      }
    )
  }, [])
  return <Router>
    {(
    <div>
        <Search/>
         <h1>Title: {pageDetails?.title}</h1>
        <h2>Page: {pageDetails?.pagenum}</h2>
        <h2>{pageDetails?.content}</h2>
        <button onClick={this.sayHello}>
      Click me!
    </button>
    </div>
  )} </Router>
}
export default App;
