import { gql } from "@apollo/client";

export function fetchFirstPage (title:String) {
  return gql`
  query fetchBookIdFromTitle {
    allBooks(
      condition: { 
        title: "${title}"
      }
    ){
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;
}

export const FETCH_BOOK_ID_FROM_TITLE = gql`
  query fetchBookIdFromTitle {
    allBooks(
      condition: { 
        title: "Harry Potter"
      }
    ) {
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;


export const TEST = gql `
  query test {
    allPages {
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;

export const TEST2 = gql `
  query test2($id: Int!) {
    pageById(id: $id){
      bookId
      pagenum
      content
    }
  }
`;


export const FETCH_FIRST_PAGE = gql `
  query test3($id: Int!) {
    bookById(id: $id){
      pagesByBookId{
        nodes{
          pagenum
          content
        }
      }
    }
  }
`;
