import { gql } from "@apollo/client";

export const fetchBookIdFromTitle = gql`
  query fetchBookIdFromTitle {
    allBooks
  }
`;

export const fetchFirstPage = gql`
  query fetchFirstPage {
      allPages(
        condition: { 
          bookId: 1
          pagenum:1
          }
      ) {
        edges {
          node {
            bookId
            pagenum
            content
          }
        }
      }
    }
`;

export const test = gql `
  query test {
    allPages {
      edges {
        node {
          bookId
          pagenum
          content
        }
      }
    }
  }
`;
