import React, { useState, useEffect} from 'react';
import Page from './page';
import Navbar from './navbar';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import './App.css';

const App = () => {
  const [pageDetails, setData] = useState(null);
  const [currPage, setCurrPage] = useState(1);


  // console.log(query);
  useEffect(() => {
    // invalid url will trigger an 404 error
    const { search } = window.location;
    const query = new URLSearchParams(search).get('book');
    fetch("/getpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(query)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    )
  }, []);

  function getNextPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage+1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    );
    setCurrPage(currPage + 1);
  };
  function getPrevPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage-1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    );
    setCurrPage(currPage - 1);
  }

  return <Router>
    {(
    <div>
      <Navbar title = {pageDetails?.title}/>
      <Routes>
        <Route path="/home" element={<Home />} />

       </Routes>
    </div>
  )} </Router>
}

function Home() {
  return <h1>SEARCH FOR A BOOK</h1>;
}

function Pages() {
  return ( <Route path="/page">
  <Page 
    contents={pageDetails?.content}
    pagenum = {pageDetails?.pagenum}
  />
  <h2 className = "pagenum">page: {pageDetails?.pagenum}</h2>
  <button className = "circleBackButton" onClick={getPrevPage}>
    back page
  </button>
  <button className = "circleForwardButton" onClick={getNextPage}>
    next page
  </button>
</Route>);
}

export default App;
