import React, { useState, useEffect } from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import { useHistory,useLocation } from "react-router-dom";
import {Navbar, Intro, SearchResult, Search} from './components/index';
import './App.css';

const App = () => {
  // const [pageDetails, setData] = useState({title: '', pagenum: '', content: ''});

  //searches path for keyword book and queries the string after = sign
  const location = useLocation();
  const query = new URLSearchParams(location.search).get("book");
  //regex to replace space with + sign
  const [title, setTitle] = useState(query?.replace(/\s/g, '+'));

  let history = useHistory();

  // renders by default when state changes. Depending on [], it will run once, or every time a state is changed
  useEffect(() => {
  // redirect to a different path which is urlSearchParam compatible
  // this is done so that infinite rendering does not occur with the useEffect hook
    function handleClick(title: String) {
      history.push(`/page?read=${title}`);
    };
    if(title){
      setTitle(title);
      handleClick(title);
    }
  },[setTitle, title, history]);

  // when switch is rendered, it searches through children route. Once a url match is found, 
  // it will use that route to load its contents 
  return (
    <div>
      <Navbar/>
      <Router>
        <Switch>
          <Route path="/home">
            <Search />
            <Intro />
          </Route>
          <Route path={"/page"}>
            <SearchResult/>
          </Route>
          <Route path={"/books"}>
            <SearchResult/>
          </Route>
        </Switch>
      </Router>
    </div>
  );
}
export default App;
