import React, { useState, useEffect} from 'react'
import axios from 'axios'
import Search from './searchBook'
import { BrowserRouter as Router } from "react-router-dom";

const books = [
  { title: 'Harry Potter' },
  { title: 'Brisingr' },
];

const filterPosts = (books, query) => {
  if (!query) {
      return books;
  }
  return books.filter((book) => {
      const bookName = book.title;
      return bookName.includes(query) || bookName.toLowerCase().includes(query) ;
  });
};

function handlePostQuery(query){
  var myParams = {
      data: query
  }
  if (query != "") {
      axios.post('http://localhost:5000/api/query', myParams)
          .then(function(response){
              console.log(response);
     //Perform action based on response
      })
      .catch(function(error){
          console.log(error);
     //Perform action based on error
      });
  } else {
      alert("The search query cannot be empty")
  }
}


const App = () => {
  const { search } = window.location;
  const query = new URLSearchParams(search).get('book');
  const [searchQuery, setSearchQuery] = useState(query || '');
  const filteredPosts = filterPosts(books, searchQuery);
  return <Router>
  {(
    <div>
        <Search           
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery} 
        />
          handlePostQuery(query)

        <ul>
          {filteredPosts.map(book => (
            <li>{book.title}</li>
          ))}
        </ul>
    </div>
  )} </Router>
}

export default App;

