import React, { useState, useEffect} from 'react'
import axios from "axios"
import Search from './searchBook'

const books = [
  { id: '1', name: 'Harry Potter' },
  { id: '2', name: 'Brisingr' },
];

const filterPosts = (books, query) => {
  if (!query) {
      return books;
  }

  return books.filter((post) => {
      const postName = post.name.toLowerCase();
      return postName.includes(query);
  });
};


const App = () => {
  const { search } = window.location;
  const query = new URLSearchParams(search).get('book');
  const [searchQuery, setSearchQuery] = useState(query || '');
  const filteredPosts = filterPosts(books, searchQuery);

  return (
    <div>
        <Search           
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery} 
        />

        <ul>
          {filteredPosts.map(post => (
            <li key={post.key}>{post.name}</li>
          ))}
        </ul>
    </div>
  );
}

export default App;

