import React, { useState, useEffect, useCallback} from 'react';
import Page from './components/page';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import './App.css';
import { useHistory,useLocation } from "react-router-dom";
import {Navbar, Intro, SearchResult, Search} from './components/index';

const App = () => {
  // const [pageDetails, setData] = useState({title: '', pagenum: '', content: ''});

  const location = useLocation();
  const query = new URLSearchParams(location.search).get("book");

  const [title, setTitle] = useState("");
  // console.log(query);
  // console.log(title);

  let history = useHistory();

  const handleClick = useCallback((title: String) => {
    history.push(`/read?book=${title}`);
  },[]);

  useEffect(() => {
    if(query){
      // setData(data.pageById)
      //regex to remove space
      setTitle(query?.replace(/\s/g, '-')!);
      handleClick(title);
    }
  }, [query, title, handleClick]);

  // when switch is rendered, it searches through children route. Once a url match is found, 
  // it will use that route to load its contents 
  return (
    <div>
      <Navbar/>
      <Router>
        <Switch>
          <Route path="/home">
            <Search />
            <Intro />
          </Route>
          <Route path={"/read"}>
            <SearchResult/>
            {/* <Search /> */}
          </Route>
        </Switch>
      </Router>
    </div>
  );
}
export default App;

