import React, { useState, useEffect} from 'react';
import { useLocation } from "react-router-dom";
// import {client} from '../index'
import { useQuery } from '@apollo/client';
import { FETCH_FIRST_PAGE } from '../gql/query';


function SearchResult () {
    const [pageDetails, setPageDetails] = useState({ pagenum:0,content:"" });
    const location = useLocation();
    const query = new URLSearchParams(location.search).get("read");
    const [title, setTitle] = useState(query);
    const [bookId, setBookId] = useState(0);
    const [currPage, setCurrPage] = useState(1);
    if(title) console.log(title);

    // if(title === "Harry Potter"){
    //     setBookId(1);
    // }else if(title === "Brisingr"){
    //     setBookId(2);
    // }
    useEffect(() => {
        if(title === "Harry Potter"){
            setBookId(1);
        }else if(title === "Brisingr"){
            setBookId(2);
        }
    }, [title]);

    // useQuery will send query to backend through uri link and get the data
    const {  loading, error, data }  = useQuery(
        FETCH_FIRST_PAGE,{ variables: { id: bookId }}
    );
    // body in useEffect will run after page is rendered. 
    useEffect(() => {
        if(data){
            setPageDetails(data.bookById.pagesByBookId.nodes[0]);
            console.log(data.bookById.pagesByBookId.nodes[0])
        }
    }, [data]);
    if (loading) console.log('Loading...');
    if (error) console.log(`Error! ${error.message}`);

    function getNextPage() {
        fetch("/nextpage", {
        method:"POST",
        headers: {"Content-Type": "application/json" },
        body: JSON.stringify(currPage+1)
        }).then(res => res.json()
        ).then(
        pageDetails => {
            setData(pageDetails)
            // console.log(pageDetails)
        }
        );
        setCurrPage(currPage + 1);
    };
    function getPrevPage() {
        setCurrPage(currPage - 1)
    }

    return (
        <div className="page">
            <h1 className = "title">{title}</h1>
            <h2 className ="contents">{pageDetails.content}</h2>
            <h2 className ="pagenum-inpage">{pageDetails.pagenum}</h2>
            <button className = "circleBackButton" onClick={getPrevPage}>
              back page
            </button>
            <button className = "circleForwardButton" onClick={getNextPage}>
              next page
            </button>
        </div>
    )
}

export default SearchResult;





