import React, { useState} from "react";

// Home page big input
function HomeSearch () {
    const [search, setSearch] = useState("");
    return (
        <form action="/" method="get">
        <input
            onChange={(e) => {
                setSearch(e.target.value)
            }}
            type="text"
            id="header-search"
            placeholder="Search books"
            name="book"
            className = "introInput"
        />
        <button 
            className = "introSearch"
            >Search
        </button>
    </form>
    )
};
export default HomeSearch;
