import React, { useState, useEffect} from 'react'
import Search from './searchBook'
import Page from './page'
import { BrowserRouter as Router } from "react-router-dom";
import './App.css';

const App = () => {
  const [pageDetails, setData] = useState(null);
  const [currPage, setCurrPage] = useState(1);


  // console.log(query);
  useEffect(() => {
    // invalid url will trigger an 404 error
    const { search } = window.location;
    const query = new URLSearchParams(search).get('book');
    fetch("/getpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(query)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    )
  }, []);

  function getNextPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage+1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    );
    setCurrPage(currPage + 1);
    console.log(currPage);
  };
  function getPrevPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage-1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        console.log(pageDetails)
      }
    );
    setCurrPage(currPage - 1);
    console.log(currPage);
  }

  return <Router>
    {(
    <div>
      <Search />
      <Page user={pageDetails?.content}/>
      <h1 class = "title">{pageDetails?.title}</h1>
      <h2 class = "pagenum">page: {pageDetails?.pagenum}</h2>
      {/* <h2>{pageDetails?.content}</h2> */}
      <button class = "circleBackButton" onClick={getPrevPage}>
        back page
      </button>
      <button class = "circleforwardButton" onClick={getNextPage}>
        next page
      </button>
    </div>
  )} </Router>
}
export default App;
