import React, { useState, useEffect} from 'react';
import Page from './page';
import Navbar from './navbar';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import './App.css';
import { ApolloClient, InMemoryCache, ApolloProvider, gql } from '@apollo/client';

const client = new ApolloClient({
  uri: 'https://flyby-gateway.herokuapp.com/',
  cache: new InMemoryCache(),
});
client
  .query({
    query: gql`
      query GetLocations {
        locations {
          id
          name
          description
          photo
        }
      }
    `,
  })
  .then((result) => console.log(result));

const App = () => {
  const [pageDetails, setData] = useState({title: '', pagenum: '', content: ''});
  const [currPage, setCurrPage] = useState(1);


  // console.log(query);
  useEffect(() => {
    // invalid url will trigger an 404 error
    const { search } = window.location;
    const query = new URLSearchParams(search).get('book');
    fetch("/getpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(query)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    )
  }, []);

  function getNextPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage+1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    );
    setCurrPage(currPage + 1);
  };
  function getPrevPage() {
    fetch("/nextpage", {
      method:"POST",
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify(currPage-1)
    }).then(res => res.json()
    ).then(
      pageDetails => {
        setData(pageDetails)
        // console.log(pageDetails)
      }
    );
    setCurrPage(currPage - 1);

  }

  return <Router>
    {(
    <div>
      <Navbar title = {pageDetails?.title}/>
      <Page 
            contents={pageDetails?.content}
            pagenum = {pageDetails?.pagenum}
          />
          <h2 className = "pagenum">page: {pageDetails?.pagenum}</h2>
          <button className = "circleBackButton" onClick={getPrevPage}>
            back page
          </button>
          <button className = "circleForwardButton" onClick={getNextPage}>
            next page
          </button>
      <Switch>
        {/* <Route path="/home">
          <Search />
