import { gql } from "@apollo/client";

export function fetchFirstPage (title:String) {
  return gql`
  query fetchBookIdFromTitle {
    allBooks(
      condition: { 
        title: "${title}"
      }
    ){
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;
}


export const FETCH_FIRST_PAGE = gql `
  query test3($id: Int!) {
    bookById(id: $id){
      pagesByBookId{
        nodes{
          pagenum
          content
        }
      }
    }
  }
`;
