import { gql } from "@apollo/client";

export const FETCH_BOOK_ID_FROM_TITLE = gql`
  query fetchBookIdFromTitle {
    allBooks(
      condition: { 
        title: "Harry Potter"
      }
    ) {
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;

export const FETCH_FIRST_PAGE = gql`
  query fetchFirstPage {
      allPages(
        condition: { 
          bookId: 1
          pagenum:1
          }
      ) {
        edges {
          node {
            bookId
            pagenum
            content
          }
        }
      }
    }
`;

export const TEST = gql `
  query test {
    allPages {
      nodes {
        bookId
        pagenum
        content
      }
    }
  }
`;
