  //     handleClick(title);
  //   }
  // },[setTitle, title, history]);

  // when switch is rendered, it searches through children route. Once a url match is found, 
  // it will use that route to load its contents 
  return (
    <div>
      <Navbar/>
      <Router>
        <Switch>
          <Route path="/home">
            <Search />
            <Intro />
          </Route>
          <Route path={"/page"}>
            <SearchResult/>
          </Route>
          <Route path={"/books"}>
            <AllBooks/>
          </Route>
        </Switch>
      </Router>
    </div>
  );
}
export default App;
