import { gql } from "@apollo/client";

export const fetchBookIdFromTitle = gql`
    query fetchBookIdFromTitle {
        allBooks(
            condition: { 
                title: "Harry Potter"
                }
            ) {
            edges {
                node {
                  id
                  title
                }
            }
        }
    }
`;

export const fetchFirstPage = gql`
query fetchFirstPage {
    allPages(
      condition: { 
        bookId: 1
        pagenum:1
        }
    ) {
      edges {
        node {
          id
          pagenum
          content
          bookId
        }
      }
    }
  }
`;
